import Card from "../UI/Card";
import SweetItem from "./SweetItem/SweetItem";
import styles from "./AvailableSweets.module.css";
import Gallery from "../Gallery";

import Img1 from "../assets/images/image1.jpg";
import Img2 from "../assets/images/image2.jpg";
import Img3 from "../assets/images/image3.jpg";
import Img4 from "../assets/images/image4.jpg";
import Img5 from "../assets/images/image5.jpg";
import Img6 from "../assets/images/image6.jpg";

const SWEETS_DATA = [
  {
    id: "s1",
    name: "Orzech z karmelem",
    description:
      "biszkopt kawowy z cytryna, mus karmelowy, krem karmelowo-orzechowy",
    price: 20,
  },
  {
    id: "s2",
    name: "Financier",
    description:
      "biszkopt kawowy z cytryna, mus karmelowy, krem karmelowo-orzechowy",
    price: 17,
  },
  {
    id: "s3",
    name: "Cherry",
    description: "wiśnie, mus rumowy, marcepan",
    price: 20,
  },
  {
    id: "s4",
    name: "Madagaskar",
    description:
      "biszkopt kawowy, krem baileys, jadody, mus na czekoladzie madagaskar",
    price: 22,
  },
  {
    id: "s5",
    name: "Passion",
    description:
      "ciasto daktylowo-kokosowe, żelka mango-karakuja, mus kokosowy",
    price: 25,
  },
  {
    id: "s6",
    name: "Orange",
    description:
      "biszkopt z orzechów włoskich, żelka z pamarańczy, mus cynamowy",
    price: 24,
  },
];

const AvailableMeals = () => {
  const sweetsList = SWEETS_DATA.map((sweet) => (
    <SweetItem
      key={sweet.id}
      name={sweet.name}
      description={sweet.description}
      price={sweet.price}
      image={sweet.srcImg}
    />
  ));
  return (
    <section className={styles.sweets}>
      <Card>
        <ul>{sweetsList}</ul>
        <Gallery />
      </Card>
    </section>
  );
};
export default AvailableMeals;
