import Modal from "../UI/Modal";

const Cart = (props) => {
  const cartItems = (
    <ul className={styles["cart-items"]}>
      {[{ id: "1", name: "financier", amount: 2, price: 20 }].map((item) => (
        <li>{item.name}</li>
      ))}
    </ul>
  );

  return (
    <Modal onClose={props.onClick}>
      {cartItems}

      <div className={styles.total}>
        <span>Total amount</span>
        <span>20 PLN</span>
      </div>
